$(document).ready(function(e) {

    $('#btnValidate').click(function() {

        var sEmail = $('#txtEmail').val();

		// faccio il controllo del campo vuoto

        if ($.trim(sEmail).length == 0 || $("#fname").val()=="" || $("#lname").val()=="") {

            alert('Prego, riempire il campo con una mail valida');

            e.preventDefault();

        }

        if (validateEmail(sEmail)) {

            alert('Registrazione avvenuta con successo');

        }

        else {

            alert('Email inserita non valida!');

            e.preventDefault();

        }

    });

});



// Funzione che convalida l'indirizzo email tramite un'espressione regolare.

function validateEmail(sEmail) {

	  var filter =  /^[\w\-\.\+]+\@[a-zA-Z0-9\.\-]+\.[a-zA-z0-9]{2,4}$/;

    

   if (filter.test(sEmail)) {

        return true;

    }

    else {

        return false;

    }

}
